<template>
  <div class="search_body">
    <div class="search_input">
      <div class="search_input_wrapper">
        <i class="iconfont icon-sousuo"></i>
        <input type="text" v-model="searchData"/>
      </div>
    </div>
    <div class="search_result">
      <h3 v-if="this.movieList.length">电影/电视剧/综艺</h3>
      <ul>
        <li v-for="item in movieList" :key="item.id" @touchend="handleToDetail(item.id)">
          <div class="img"><img :src="item.img | filterImgUrl('@1l_1e_1c_128w_180h')" /></div>
          <div class="info">
            <p><span>{{item.nm}}<span v-if="item.version" :class="'version ' + item.version"></span></span>&nbsp;<span>{{item.sc}}分</span></p>
            <p>{{item.enm}}</p>
            <p>{{item.cat}}</p>
            <p>{{item.rt}}</p>
          </div>
        </li>
      </ul>
    </div>
    <div v-if="this.movieList.length && this.cinemaList.length" class="bg"></div>
    <div class="cinema_body">
      <h3 v-if="this.cinemaList.length">影院</h3>
      <ul>
        <li v-for="item in cinemaList" :key="item.id">
        <div>
          <span>{{item.nm}}</span>
          <span class="q"><span class="price">{{item.sellPrice}}</span> 元起</span>
        </div>
        <div class="address">
          <span>{{item.addr}}</span>
          <span>{{item.distance}}</span>
        </div>
        <div class="card">
          <div v-if="item.sell" class="bl">座</div>
          <div v-if="item.allowRefund" class="bl">退</div>
          <div v-if="item.endorse" class="bl">改签</div>
          <div v-if="item.snack" class="or">小吃</div>
          <div v-for="(data, index) in item.hallType" :key="index" class="bl">{{data}}</div>
        </div>
      </li>
      </ul>
    </div>
  </div>
</template>

<script>
import axios from 'axios'
export default {
  data () {
    return {
      searchData: '',
      cinemaList: [],
      movieList: []
    }
  },
  watch: {
    searchData () {
      axios.get('/ajax/search?kw=' + encodeURI(this.searchData) + '&cityId=' + this.$store.state.city.cityId + '&stype=-1').then(res => {
        if (JSON.stringify(res.data) !== '{}') {
          this.cinemaList = res.data.cinemas.list
          this.movieList = res.data.movies.list
        }
      })
    }
  },
  methods: {
    handleToDetail (moiveId) {
      this.$router.push('/detail/' + moiveId)
    }
  }
}
</script>

